import React, { useState } from "react";
import emailjs from "emailjs-com";
import {useForm} from "react-hook-form";

const Contacto = () => {

    const [sucessMessage, setSuccessMessage] = useState("");

    const { register, handleSubmit, errors } = useForm();

    const serviceID = "service_wqvaobd";
    const templateID = "template_5dnjfyo";
    const userID = "user_Y1NXuqBbXSj0BTdA8kb4E";

    const onSubmit = (data, r) => {
        sendEmail (
            serviceID,
            templateID,
            {
                name: data.name,
                telefono: data.telefono,
                email: data.email,
                subject: data.subject,
                description: data.description
            },
            userID
        )
            r.target.reset();
    }


    const sendEmail = (e) => {
        e.preventDefault();

        emailjs.send(serviceID, templateID, variables, userID)
            .then(() => {
                setSuccessMessage("¡Muchas gracias por el contacto! Le responderemos lo antes posible.")
            }).catch(err => console.error(`Algo ha ido mal... ${err}`));
    }




    return (
        <div className="contacts">
            <div className="text-center">
            <h1>¡Contacta con nosotros!</h1>
            <p>aqui va un texto de puta madre que tiene que hacer la valeria</p>
            </div>
            <div className="container">
                <form onSubmit={handleSubmit(onSubmit)}>
                <div className="row">
                    <div className="col-md-6 col-xs-12">
                        {/* Nombre */}
                        <div className="text-center">                                           
                        <input
                        id="name"
                        type="text"
                        className="form-control"
                        placeholder="Nombre"
                        name="name"
                        ref={
                            register({
                            required: "Introduce tu nombre",
                            maxLength: {
                                value: 20,
                                message: "Ups! Un máximo de 20 caracteres"

                                }
                            })
                        }
                        />
                        <div className="line"></div>
                        </div>

                        {/* Teléfono */}
                        <div className="text-center">
                        <input
                        id="telefono"
                        type="text"
                        className="form-control"
                        placeholder="Telefono"
                        name="telefono"
                        />
                        </div>
                        <div className="line"></div>
                        {/* Email */}
                        <div className="text-center">
                        <input
                        id="mail"
                        type="email"
                        className="form-control"
                        placeholder="Email"
                        name="email"
                        />
                        </div>
                        <div className="line"></div>
                        {/* subject */}
                        <div className="text-center">
                        <input
                        id="subject"
                        type="text"
                        className="form-control"
                        placeholder="¡Cúentanos tu proyecto!"
                        name="subject"
                        />
                        </div>
                        <div className="line"></div>
                    </div>
                    <div className="col-md-6 col-xs-12">
                        {/* Descripcion */}
                        <div className="text-center">
                        <textarea
                        id="description"
                        type="text"
                        className="form-control"
                        placeholder="Descripción"
                        name="descripcion"
                        ></textarea>
                        </div>
                        <div className="line"></div>
                        <button className="btn-main-offer contact-btn" type="submit">Enviar</button>
                    </div>
                </div>
                </form>
            </div>
        </div>
    )
}

export default Contacto
